const authReducer = (state, action) => {
    switch (action.type) {
        case "TOKEN_RECEIVED":
            return {
                ...state,
                token: action.payload
            };
        default:
            return state;
    }
}

const loginReducer = (state, action) => {
    switch (action.type) {
        case "EMAIL_CHANGED":
            return {
                ...state, email: action.payload
            };
        case "PASSWORD_CHANGED":
            return {
                ...state, password: action.payload
            };
        default:
            return state;
    }
}

export {authReducer, loginReducer};
