import React from 'react';
import {Link} from "react-router-dom";
import {useCartContext} from "../../Context/CardContext";
import {useAuthContext} from '../../Context/AuthContext';


const VerticalCard= (props) => {
    const {authState:{token}, authDispatch} = useAuthContext();

    const data = props.data;
    const {cartState:{cart}, cartDispatch} = useCartContext();

    return (
        <div className="card-horizontal-container">
            <div className="card-horizontal">
                <div className="card-col">
                    <img className="card-horizontal-img" src={data.image} alt="img"/>
                </div>
                <div className="card-col">
                    <div className="card-details">
                        <div className="name">
                        <h2 className="name-heading">{data.title}</h2>
                        <h4 className="name-sub-heading">by ApniDukan</h4>
                        </div>
                        <p className="price">${data.price}
                            <small className="text-striked">${data.original_price}</small>
                        </p>
                        <p>
                            <button className="decrease" disabled={data.quantity===1 && "disabled"} onClick={()=>cartDispatch({type:"DECREASE_COUNT", payload:data})}>-</button>
                            <span>{data.quantity}</span>
                            <button className="increase" onClick={()=>cartDispatch({type:"INCREASE_COUNT", payload:data})}>+</button>
                        </p>
                        <button className="btn btn-icon success-icon danger-icon cart" onClick={ () => cartDispatch({type:"REMOVE_FROM_CART", payload:data})} >
                            <i className="fas fa-trash"></i>
                            <span>Delete</span>
                        </button>
                        <Link to="/WishListPage"><button className="btn btn-icon success-icon cart">
                            <span>Save for Later</span>
                        </button></Link>
                    </div>
                </div>
            </div>
        </div>	
    )
}

export {VerticalCard};
