import React, {useContext, useState, createContext, useReducer, useEffect} from 'react';
import {addToCartReducer, getDataReducer} from './../Reducer/addToCartReducer';
import {wishListReducer} from './../Reducer/wishListReducer';
import {filterReducer} from './../Reducer/filterReducer';
import { getData } from '../api-Calls/api-utility';

const CartContext = createContext();
const useCartContext = () =>  useContext(CartContext);

const CartProvider = ({children}) => {
    const [cartState, cartDispatch] = useReducer(addToCartReducer, {cart: []});
    const [getProduct, getProductDispatch] = useReducer(getDataReducer, {products: []});
    const [wishState, wishDispatch] = useReducer(wishListReducer, {wishList: []});
    const [filterState, filterDispatch] = useReducer(filterReducer, {filterByRating: 0, filterByCategory:[], filterBySortBy:"", filterByPriceRange:5000});

    /**
     * @description: This function is used to add product to cart, wishlist and filter
     * @param {Object} data: {cart}, {wishlist}, {filter}
     * @returns: Object
     */


    // const [productData] = useState(Data.products);
    useEffect(() => {
        (async () => {
        const productData = await getData();
        console.log(productData);
        getProductDispatch({type: "GET_DATA", payload: productData});
    })();
    }, []);


    return <CartContext.Provider value={{cartState, cartDispatch, wishState, wishDispatch, filterState, filterDispatch}}>
            {children}
        </CartContext.Provider>;
};

export {CartProvider, useCartContext};
